import React, { Component } from 'react';
import io from 'socket.io-client';

class App extends Component {
  constructor() {
    super();

    this.state = {
      endpoint: "http://localhost:5000",
      pulseOx: []
    };

    this.createConnection();
  }

  createConnection = () => {
    this.socket = io(this.state.endpoint);

    this.socket.on('data', data => {
      this.addData(data);
    });
  }

  addData = data => {
    data = parseInt(data.slice(0, -1));
    let newPulseOx = this.state.pulseOx.concat(data);

    if (this.state.pulseOx.length > 30) {
      newPulseOx = newPulseOx.slice(-30);
    }

    this.setState({ pulseOx: newPulseOx });
  }

  render() {
    console.log(this.state.pulseOx);
    const { pulseOx } = this.state;
    return (
      <div>
        <h1>Current pulse oximetry readings: {pulseOx.length == 0 ? 0 : pulseOx[pulseOx.length-1]}%</h1>
      </div>
    );
  }
}

export default App;
